import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMoon, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

function HomePage() {
  const Api_key = "71146ebefc69425aa26e16e6e9e066d1";
  const url = "https://newsapi.org/v2/everything?q=";
  const [currentLanguage] = useState('en');
  const [articles, setArticles] = useState([]);
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const [currentNav, setCurrentNav] = useState(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    fetchNews("India");
  }, []);

  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]);

  const fetchNews = async (query) => {
    try {
      const res = await fetch(`${url}${query}&apiKey=${Api_key}&language=${currentLanguage}`);
      if (!res.ok) {
        throw new Error(`Network response was not ok: ${res.statusText}`);
      }
      const data = await res.json();
      if (data.articles.length === 0) {
        alert('No articles found');
      } else {
        setArticles(data.articles.filter(article => article.urlToImage));
      }
    } catch (error) {
      alert('Error fetching news: ' + error.message);
      console.error('Error fetching news:', error);
    }
  };

  const handleNavItemClick = (id) => {
    fetchNews(id);
    if (searchInputRef.current) {
      searchInputRef.current.value = '';
    }
    setCurrentNav(id);
  };

  const handleSearch = () => {
    const query = searchInputRef.current?.value.trim();
    if (!query) {
      alert('Please enter a valid search term');
      return;
    }
    fetchNews(query);
    setCurrentNav(null);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  const toggleDarkMode = () => {
    setDarkMode(prev => !prev);
  };

  const handleReload = () => window.location.reload();

  const NewsCard = ({ article }) => {
    const date = new Date(article.publishedAt).toLocaleString("en-UK", {
      timeZone: "Asia/Jakarta"
    });

    return (
      <div className="card" onClick={() => window.open(article.url, "_blank")}>
        <div className="card-header">
          <img src={article.urlToImage} alt="News Image" id="news-img" />
        </div>
        <div className="card-content">
          <h3 id="news-title">{article.title}</h3>
          <h6 className="news-source">{`${article.source.name} ${date}`}</h6>
          <p className="news-des">{article.description}</p>
        </div>
      </div>
    );
  };

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <a href="#" onClick={handleReload} className="company-logo">
            <img src="logo16-1.png" alt="Logo" />
          </a>

          <div className="nav-links" id="nav-links">
            <ul className="flex">
              {/* <li style={{ animationDelay: '0s' }}><a href="../Weather-Forecast/main.html">Weather</a></li> */}
              <li style={{ animationDelay: '0.1s' }}><a href="../Add Note/">Add Note</a></li>
              <li 
                style={{ animationDelay: '0.2s' }}
                className={`hover-link nav-item ${currentNav === 'highlights' ? 'active' : ''}`} 
                id="highlights" 
                onClick={() => handleNavItemClick('highlights')}
              >
                Highlights
              </li>
              <li style={{ animationDelay: '0.3s' }}><a href="../bot/index.html">Bot</a></li>
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input 
                id="search-text" 
                type="text" 
                className="news-input" 
                placeholder="Enter.." 
                onKeyPress={handleKeyPress}
                ref={searchInputRef}
              />
              <button className="search-button" id="search-button" onClick={handleSearch}>
                <img src="srch.png" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div className="toggle-button dark-mode-button" onClick={toggleDarkMode}>
                <div className="icons">
                  <FontAwesomeIcon icon={faMoon} />
                </div>
              </div>
              <div 
                id="user-info" 
                className="user-info" 
                style={{ marginTop: '2px', marginLeft: '8px', fontSize: '30px' }}
              >
                <a href="../login and signup/logout.php" className="logout-btn">
                  <div className="icons">
                    <FontAwesomeIcon icon={faSignOutAlt} style={{ color: 'cadetblue' }} />
                  </div>
                </a>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="scrollable-container">
          <div className="cards-container container flex" id="cards-container">
            {articles.map((article, index) => (
              <NewsCard key={index} article={article} />
            ))}
          </div>
        </div>
      </main>
    </>
  );
}

export default HomePage;
