import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMoon, faSun, faSignOutAlt } from "@fortawesome/free-solid-svg-icons";
import "./style.css";

const API_KEY = process.env.REACT_APP_NEWS_API_KEY || "fallback-api-key"; // Replace with actual fallback or handle error
const API_URL = "https://newsapi.org/v2/everything?q=";

const NewsApp = () => {
  const [articles, setArticles] = useState([]);
  const [query, setQuery] = useState("India");
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const searchRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchNews(query);
  }, [query]);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const fetchNews = async (q) => {
    try {
      if (!API_KEY) {
        throw new Error("News API key is missing. Please configure REACT_APP_NEWS_API_KEY.");
      }
      const res = await fetch(`${API_URL}${q}&apiKey=${API_KEY}&language=en`);
      if (!res.ok) {
        throw new Error(`Failed to fetch news: ${res.status} ${res.statusText}`);
      }
      const data = await res.json();
      if (data.status === "error") {
        throw new Error(data.message || "Error from News API");
      }
      if (data.articles.length === 0) {
        alert("No articles found for this query");
        setArticles([]);
      } else {
        setArticles(data.articles.filter((a) => a.urlToImage));
      }
    } catch (err) {
      alert(`Error fetching news: ${err.message}`);
      console.error(err);
      setArticles([]);
    }
  };

  const handleSearch = () => {
    const val = searchRef.current.value.trim();
    if (!val) return alert("Please enter a valid search term");
    setQuery(val);
    searchRef.current.value = "";
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") handleSearch();
  };

  const handleNavClick = (section) => {
    setQuery(section);
    if (searchRef.current) searchRef.current.value = "";
  };

  const handleLogout = async () => {
    try {
      const res = await fetch("/api/logout", { method: "POST" });
      if (res.ok) {
        localStorage.removeItem("token");
        navigate("/login");
      } else {
        alert("Logout failed");
      }
    } catch (err) {
      console.error("Logout error:", err);
      alert("Error during logout");
    }
  };

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <Link to="/" className="company-logo" onClick={() => handleNavClick("India")}>
            <img src="/logo16-1.png" alt="Logo" />
          </Link>

          <div className="nav-links" id="nav-links">
            <ul className="flex">
              <li><Link to="/weather">Weather</Link></li>
              <li><Link to="/add-note">Add Note</Link></li>
              <li className="hover-link nav-item" onClick={() => handleNavClick("Highlights")}>
                Highlights
              </li>
              <li><Link to="/bot">Bot</Link></li>
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input
                ref={searchRef}
                type="text"
                className="news-input"
                placeholder="Enter..."
                onKeyPress={handleKeyPress}
              />
              <button className="search-button" onClick={handleSearch}>
                <img src="/srch.png" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div
                className="toggle-button dark-mode-button"
                onClick={() => setDarkMode((prev) => !prev)}
              >
                <div className="icons">
                  <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
                </div>
              </div>

              <div className="user-info" style={{ marginTop: "2px", marginLeft: "8px", fontSize: "30px" }}>
                <button className="logout-btn" onClick={handleLogout}>
                  <div className="icons">
                    <FontAwesomeIcon icon={faSignOutAlt} style={{ color: "cadetblue" }} />
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="scrollable-container">
          <div className="cards-container container flex">
            {articles.map((article, idx) => (
              <div className="card" key={idx} onClick={() => window.open(article.url, "_blank")}>
                <div className="card-header">
                  <img src={article.urlToImage} alt="News" />
                </div>
                <div className="card-content">
                  <h3>{article.title}</h3>
                  <h6 className="news-source">
                    {article.source.name}{" "}
                    {new Date(article.publishedAt).toLocaleString("en-UK", {
                      timeZone: "Asia/Jakarta",
                    })}
                  </h6>
                  <p className="news-des">{article.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </>
  );
};

export default NewsApp;
