import React, { useState, useEffect, useRef, useCallback } from 'react';

// News Card Component
const NewsCard = ({ article }) => {
  const date = new Date(article.publishedAt).toLocaleString("en-GB", {
    hour: '2-digit',
    minute: '2-digit',
    day: '2-digit',
    month: 'short',
    year: 'numeric'
  });

  return (
    <div className="card" onClick={() => window.open(article.url, "_blank")}>
      <div className="card-header">
        <img src={article.urlToImage} alt={article.title || "News Image"} id="news-img"
          onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/400x230/e0e0e0/505050?text=No+Image`; }}
        />
      </div>
      <div className="card-content">
        <h3 id="news-title">{article.title}</h3>
        <h6 className="news-source">{`${article.source.name} ${date}`}</h6>
        <p className="news-des">{article.description}</p>
      </div>
    </div>
  );
};

const HomePage = () => {
  const Api_key = "71146ebefc69425aa26e16e6e9e066d1"; // Your NewsAPI key
  const url = "https://newsapi.org/v2/everything?q=";

  const [articles, setArticles] = useState([]);
  const [currentLanguage] = useState('en'); // Assuming English as default from script.js
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const [currentNav, setCurrentNav] = useState(null);
  const [message, setMessage] = useState(''); // For user feedback messages

  const searchInputRef = useRef(null);

  // Effect to load initial news and apply dark mode class on body
  useEffect(() => {
    // Apply dark mode class based on state
    document.body.classList.toggle('dark-mode', darkMode);

    // Initial fetch for "India" news
    fetchNews("India");
  }, []); // Runs once on component mount

  // Effect to update dark mode preference in localStorage and apply to body
  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]); // Runs whenever darkMode state changes

  // Function to fetch news articles from NewsAPI
  const fetchNews = useCallback(async (query) => {
    setMessage(''); // Clear any previous messages
    try {
      const res = await fetch(`${url}${query}&apiKey=${Api_key}&language=${currentLanguage}`, {
        headers: {
          'User-Agent': 'News-App/1.0' // Required by NewsAPI for some requests
        }
      });

      if (!res.ok) {
        // Handle specific error codes like 426 or general network errors
        if (res.status === 426) {
          setMessage('Error: Your API key might be invalid or have exceeded its usage limits. Please check your NewsAPI key.');
        } else {
          setMessage(`Error fetching news: ${res.statusText || 'Unknown Error'} (Status: ${res.status}). Please check your internet connection or try again later.`);
        }
        throw new Error(`Network response was not ok: ${res.statusText} (Status: ${res.status})`);
      }

      const data = await res.json();
      if (data.articles && data.articles.length > 0) {
        // Filter out articles without images as per original script.js
        const articlesWithImages = data.articles.filter(article => article.urlToImage);
        setArticles(articlesWithImages);
        if (articlesWithImages.length === 0) {
            setMessage('No articles with images found for your query. Try a different search term.');
        }
      } else {
        setArticles([]); // Clear articles if none found
        setMessage('No articles found for your query. Try a different search term.');
      }
    } catch (error) {
      console.error('Error fetching news:', error);
      // Ensure message is updated only if it's not already a specific API key error
      if (!message.includes("API key")) {
        setMessage(`Error fetching news: ${error.message}. Please check your internet connection or try again later.`);
      }
      setArticles([]); // Clear articles on error
    }
  }, [currentLanguage, message]); // Dependencies for useCallback

  // Handler for navigation item clicks
  const onNavItemClick = useCallback((id) => {
    fetchNews(id); // Fetch news based on navigation item ID
    if (searchInputRef.current) {
      searchInputRef.current.value = ''; // Clear search bar on nav click
    }
    setCurrentNav(id); // Set active nav item
  }, [fetchNews]);

  // Handler for search button click
  const handleSearch = useCallback(() => {
    const query = searchInputRef.current?.value.trim();
    if (!query) {
      setMessage('Please enter a valid search term.');
      return;
    }
    fetchNews(query);
    setCurrentNav(null); // Clear active nav item
  }, [fetchNews]);

  // Handler for Enter key press in search input
  const handleKeyPress = useCallback((event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  }, [handleSearch]);

  // Toggle dark mode function
  const toggleDarkMode = useCallback(() => {
    setDarkMode(prevMode => !prevMode);
  }, []);

  // Reload function
  const reload = useCallback(() => {
    window.location.reload();
  }, []);

  return (
    <>
      {/* Embedded CSS from style.css */}
      <style>{`
