import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './RegisterPage.css';

export default function RegisterPage() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Debugging: Confirm the component mounts
  useEffect(() => {
    console.log("RegisterPage component mounted successfully");
  }, []);

  const signUpUser = (e) => {
    e.preventDefault();
    console.log("Form submitted with:", { firstName, lastName, email, password });

    if (!email) {
      alert("Email has been left blank!");
      return;
    }
    if (!password) {
      alert("Password has been left blank!");
      return;
    }

    navigate("/login");
  };

  return (
    <div className="container" id="signup">
      <h1 className="form-title">Register</h1>
      <p style={{ color: "red", fontSize: "20px" }}>Debug: This text should be visible</p>
      <form onSubmit={signUpUser}>
        <div className="input-group">
          <label htmlFor="FirstName">First Name</label>
          <input
            type="text"
            id="FirstName"
            placeholder="Enter your first name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="LastName">Last Name</label>
          <input
            type="text"
            id="LastName"
            placeholder="Enter your last name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="SignupEmail">Email</label>
          <input
            type="email"
            id="SignupEmail"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="SignupPassword">Password</label>
          <input
            type="password"
            id="SignupPassword"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn">Sign Up</button>
      </form>
      <div className="links">
        <p>Already have an account?</p>
        <button onClick={() => navigate("/login")}>Sign In</button>
      </div>
    </div>
  );
}
