import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css'; // Updated to match the intended CSS file name

const HomePage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const [articles, setArticles] = useState([]);
  const [currSelNav, setCurrSelNav] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const searchTextRef = useRef(null);
  const navLinksRef = useRef(null);

  const Api_key = process.env.REACT_APP_NEWS_API_KEY || "71146ebefc69425aa26e16e6e9e066d1";
  const url = "https://newsapi.org/v2/everything?q=";
  let currentLanguage = 'en';

  // Fetch news function
  const fetchNews = async (query) => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${url}${query}&apiKey=${Api_key}&language=${currentLanguage}`);
      if (!res.ok) {
        throw new Error(`Network response was not ok: ${res.statusText}`);
      }
      const data = await res.json();
      if (data.articles.length === 0) {
        setError('No articles found');
      } else {
        bindData(data.articles);
      }
    } catch (error) {
      setError('Error fetching news: ' + error.message);
      console.error('Error fetching news:', error);
    } finally {
      setLoading(false);
    }
  };

  // Bind data to cards
  const bindData = (articlesData) => {
    setArticles(articlesData.filter(article => article.urlToImage));
  };

  // Fill data in a card (used for rendering in JSX)
  const fillDataInCard = (article) => {
    const date = new Date(article.publishedAt).toLocaleString("en-UK", {
      timeZone: "Asia/Jakarta"
    });

    return {
      image: article.urlToImage,
      title: article.title,
      description: article.description,
      source: `${article.source.name} ${date}`,
      url: article.url
    };
  };

  // Navigation item click handler
  const onNavItemClick = (id) => {
    fetchNews(id);
    clearSearchBar();
    if (currSelNav) {
      document.getElementById(currSelNav)?.classList.remove("active");
    }
    const navItem = document.getElementById(id);
    navItem?.classList.add("active");
    setCurrSelNav(id);
  };

  // Search handler
  const handleSearch = () => {
    const query = searchTextRef.current.value.trim();
    if (!query) {
      alert('Please enter a valid search term');
      return;
    }
    fetchNews(query);
    if (currSelNav) {
      document.getElementById(currSelNav)?.classList.remove("active");
      setCurrSelNav(null);
    }
  };

  // Keypress handler for search
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  // Clear search bar
  const clearSearchBar = () => {
    searchTextRef.current.value = '';
  };

  // Toggle dark mode
  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    localStorage.setItem("darkMode", newDarkMode);
    document.body.classList.toggle('dark-mode', newDarkMode);
  };

  // Reload function
  const reload = () => {
    window.location.reload();
  };

  // Handle logout
  const handleLogout = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (response.ok) {
        navigate('/login');
      } else {
        alert('Logout failed');
      }
    } catch (error) {
      console.error('Logout error:', error);
      alert('Failed to connect to the server.');
    }
  };

  // Handle navigation link animations
  useEffect(() => {
    if (navLinksRef.current) {
      const navLinks = navLinksRef.current.querySelectorAll('li');
      navLinks.forEach((link, index) => {
        link.style.animationDelay = `${index * 0.1}s`;
      });
    }
  }, []);

  // Fetch initial news and apply dark mode
  useEffect(() => {
    fetchNews("India");
    if (darkMode) {
      document.body.classList.add("dark-mode");
    }
  }, []);

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <a href="#" onClick={reload} className="company-logo">
            <img src="/home/logo16-1.png" alt="Logo" />
          </a>

          <div className="nav-links" id="nav-links" ref={navLinksRef}>
            <ul className="flex">
              <li>
                <a href="../Weather-Forecast/main.html">Weather</a>
              </li>
              <li>
                <a href="../Add Note/index2.html">Add Note</a>
              </li>
              <li
                className="hover-link nav-item"
                id="highlights"
                onClick={() => onNavItemClick('highlights')}
              >
                Highlights
              </li>
              <li>
                <a href="../bot/index.html">Bot</a>
              </li>
              {/* TODO: Convert Weather-Forecast, Add Note, and Bot pages to React components and use <Link> for navigation */}
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input
                id="search-text"
                type="text"
                className="news-input"
                placeholder="Enter.."
                onKeyPress={handleKeyPress}
                ref={searchTextRef}
              />
              <button className="search-button" id="search-button" onClick={handleSearch}>
                <img src="/home/srch.png" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div className="toggle-button dark-mode-button" onClick={toggleDarkMode}>
                <div className="icons">
                  <i className="fas fa-moon"></i>
                </div>
              </div>
              <div
                id="user-info"
                className="user-info"
                style={{ marginTop: '2px', marginLeft: '8px', fontSize: '30px' }}
              >
                <a onClick={handleLogout} className="logout-btn" style={{ cursor: 'pointer' }}>
                  <div className="icons">
                    <i className="fas fa-sign-out-alt" style={{ color: 'cadetblue' }}></i>
                  </div>
                </a>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="scrollable-container">
          <div className="cards-container container flex" id="cards-container">
            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}
            {!loading && !error && articles.map((article, index) => {
              const cardData = fillDataInCard(article);
              return (
                <div className="card" key={index} onClick={() => window.open(article.url, "_blank")}>
                  <div className="card-header">
                    <img src={cardData.image || '/home/400x200 dummy img.png'} alt="News Image" />
                  </div>
                  <div className="card-content">
                    <h3>{cardData.title}</h3>
                    <h6 className="news-source">{cardData.source}</h6>
                    <p className="news-des">{cardData.description}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </main>
    </>
  );
};

export default HomePage;
