import React, { useEffect, useState } from "react"; // Import useState here
import Newsitem from "./Newsitem";

const NewsBoard = ({category}) => {

	//creating use state variable
	const [articles, setArticles] = useState([]);
	useEffect(() => {
let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=${process.env.REACT_APP_API_KEY}`;		fetch(url)
		        .then(response => response.json())
				.then(data => {
			setArticles(data.articles);
		})
				.catch(error => console.error("Error fetching news:", error));
		}, []);

		return (
			<div>
				<h2 className="text-center">Latest <span className="badge bg-danger">News</span></h2>
				{articles.map((news, index) => ( //props
					<Newsitem key={index} title={news.title} description={news.description} src={news.urlToImage} url={news.url} />
				))}
			</div>
		);
	};

	export default NewsBoard;
