import React, { useState, useEffect, useRef } from 'react';
import './style.css'; // Import your custom CSS

const NewsCard = ({ article }) => {
  const date = new Date(article.publishedAt).toLocaleString("en-GB", {
    hour: '2-digit',
    minute: '2-digit',
    day: '2-digit',
    month: 'short',
    year: 'numeric'
  });
  // 03465d97f8e74619917d549b2458490e
  const Api_key = "71146ebefc69425aa26e16e6e9e066d1";
  return (
    <div className="card" onClick={() => window.open(article.url, "_blank")}>
      <div className="card-header">
        <img
          src={article.urlToImage || 'https://placehold.co/600x400/e0e0e0/505050?text=No+Image'}
          alt={article.title || "News Image"}
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = 'https://placehold.co/600x400/e0e0e0/505050?text=No+Image';
          }}
        />
      </div>
      <div className="card-content">
        <h3>{article.title}</h3>
        <p className="news-source">{`${article.source?.name || 'Unknown'} - ${date}`}</p>
        <p>{article.description}</p>
      </div>
    </div>
  );
};

const HomePage = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    fetchNews("india"); // Default search term
  }, []);

  const fetchNews = async (query) => {
    setLoading(true);
    setError(null);
    try {
      // IMPORTANT: Replace with your actual NewsAPI key
      const response = await fetch(
        `https://newsapi.org/v2/everything?q=${query}&apiKey=${{}}&pageSize=20`
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      if (data.articles && data.articles.length > 0) {
        setArticles(data.articles.filter(article => article.title && article.url));
      } else {
        setError('No articles found. Try a different search term.');
      }
    } catch (err) {
      setError(`Failed to fetch news: ${err.message}`);
      console.error("Error fetching news:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    const query = searchInputRef.current?.value.trim();
    if (query) {
      fetchNews(query);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="scrollable-container">
      <nav>
        <div className="main-nav">
          <div className="company-logo">
            <img src="logo16-1.png" alt="Company Logo" />
          </div>
          <div className="nav-links">
            <ul>
              <li>Add Note</li>
              <li onClick={() => fetchNews("highlights")}>Highlights</li>
              <li>Bot</li>
            </ul>
          </div>
          <div className="search-bar assign">
            <input
              type="text"
              className="news-input"
              placeholder="Search news..."
              ref={searchInputRef}
              onKeyPress={handleKeyPress}
            />
            <button className="search-button" onClick={handleSearch}>
              <img src="srch.png" alt="Search" />
            </button>
          </div>
        </div>
      </nav>

      <main>
        {loading && <p>Loading news...</p>}
        {error && <p className="error">{error}</p>}

        <div className="cards-container">
          {articles.map((article, index) => (
            <NewsCard key={`${article.url}-${index}`} article={article} />
          ))}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
