import React, { useState, useEffect, useRef } from 'react';
import './style.css';

const HomePage = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const searchInputRef = useRef(null);

  useEffect(() => {
    fetchNews("technology"); // Default news category
  }, []);

  const fetchNews = async (query) => {
    setLoading(true);
    try {
      const response = await fetch(
        `https://newsapi.org/v2/everything?q=${query}&apiKey=71146ebefc69425aa26e16e6e9e066d1&pageSize=12`
      );
      const data = await response.json();
      setArticles(data.articles || []);
    } catch (error) {
      console.error("Error fetching news:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    const query = searchInputRef.current?.value.trim();
    if (query) fetchNews(query);
  };

  return (
    <div className="app-container">
      {/* Compact Header */}
      <header className="compact-header">
        <div className="logo-container">
          <img src="logo16-1.png" alt="News Logo" className="logo" />
        </div>
        <div className="search-container">
          <input
            type="text"
            ref={searchInputRef}
            placeholder="Search news..."
            className="search-input"
          />
          <button onClick={handleSearch} className="search-button">
            Search
          </button>
        </div>
      </header>

      {/* News Cards Grid */}
      <div className="news-grid">
        {loading ? (
          <div className="loading">Loading news...</div>
        ) : articles.length > 0 ? (
          articles.map((article, index) => (
            <div key={index} className="news-card">
              {article.urlToImage && (
                <img 
                  src={article.urlToImage} 
                  alt={article.title} 
                  className="news-image"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
                  }}
                />
              )}
              <div className="news-content">
                <h3 className="news-title">{article.title}</h3>
                <p className="news-source">
                  {article.source?.name || 'Unknown'} â€¢ {new Date(article.publishedAt).toLocaleDateString()}
                </p>
                <p className="news-description">
                  {article.description?.substring(0, 100)}...
                </p>
              </div>
            </div>
          ))
        ) : (
          <div className="no-articles">No articles found</div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
