import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMoon, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

function homePage() {
//03465d97f8e74619917d549b2458490e
const Api_key = "71146ebefc69425aa26e16e6e9e066d1";
const url = "https://newsapi.org/v2/everything?q=";
let currentLanguage = 'en';

document.addEventListener("DOMContentLoaded", () => {
    const template = document.getElementById("template_card");
    const container = document.getElementById("cards-container");
    const searchText = document.getElementById("search-text");
    const searchButton = document.getElementById("search-button");
    const toggleButton = document.querySelector(".dark-mode-button");
    const logo = document.querySelector('.company-logo');
    // const languageSelect = document.getElementById("language-select");
    let darkMode = localStorage.getItem("darkMode") === "true";

    if (darkMode) {
        document.body.classList.add("dark-mode");
    }

    fetchNews("India");

    async function fetchNews(query) {
        try {
            const res = await fetch(`${url}${query}&apiKey=${Api_key}&language=${currentLanguage}`);
            if (!res.ok) {
                throw new Error(`Network response was not ok: ${res.statusText}`);
            }
            const data = await res.json();
            if (data.articles.length === 0) {
                alert('No articles found');
            } else {
                bindData(data.articles);
            }
        } catch (error) {
            alert('Error fetching news: ' + error.message);
            console.error('Error fetching news:', error);
        }
    }

window.fetchNews=fetchNews;
    function bindData(articles){
        container.innerHTML = "";

        articles.forEach((article) => {
            if (!article.urlToImage) return;
            const clone_card = template.content.cloneNode(true);
            fillDataInCard(clone_card, article);
            container.appendChild(clone_card);
        });
    }

    function fillDataInCard(cln_card, article) {
        const nws_img = cln_card.querySelector('#news-img');
        const nws_title = cln_card.querySelector('#news-title');
        const nws_src = cln_card.querySelector('.news-source');
        const nws_descr = cln_card.querySelector('.news-des');

        nws_img.src = article.urlToImage;
        nws_title.innerHTML = article.title;
        nws_descr.innerHTML = article.description;

        const date = new Date(article.publishedAt).toLocaleString("en-UK", {
            timeZone: "Asia/Jakarta"
        });

        nws_src.innerHTML = `${article.source.name} ${date}`;
        cln_card.firstElementChild.addEventListener("click", () => {
            window.open(article.url, "_blank");
        });
    }

    let currSelNav = null;
    function onNavItemClick(id) {
        fetchNews(id);
        clearSearchBar();
        if (currSelNav) {
            currSelNav.classList.remove("active");
        }
        const navItem = document.getElementById(id);
        navItem.classList.add("active");
        currSelNav = navItem;
    }

    searchButton.addEventListener("click", () => {
        handleSearch();
    });

    searchText.addEventListener("keypress", handleKeyPress);

    function handleKeyPress(event) {
        if (event.key === 'Enter') {
            handleSearch();
        }
    }

    function handleSearch() {
        const query = searchText.value.trim();
        if (!query) {
            alert('Please enter a valid search term');
            return;
        }
        fetchNews(query);
        currSelNav?.classList.remove("active");
        currSelNav = null;
    }

    function clearSearchBar() {
        searchText.value = '';
    }

    function toggleDarkMode() {
        darkMode = !darkMode;
        localStorage.setItem("darkMode", darkMode);
        document.body.classList.toggle('dark-mode', darkMode);
    }

    toggleButton.addEventListener("click", toggleDarkMode);
    document.querySelectorAll('.nav-item').forEach(item => {
        item.addEventListener('click', () => onNavItemClick(item.id));
    });

    function reload() {
        window.location.reload();
    }

    const reloadButton = document.querySelector('.reload-button');
    if (reloadButton) {
        reloadButton.addEventListener('click', reload);
    }

    if (logo) {
        logo.addEventListener('click', reload);
    }
});

document.addEventListener('DOMContentLoaded', () => {
    const navLinks = document.querySelectorAll('#nav-links ul li');
    navLinks.forEach((link, index) => {
        link.style.animationDelay = `${index * 0.1}s`;
    });
});




  const handleReload = () => window.location.reload();
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      console.log('Search triggered:', event.target.value);
    }
  };
  const handleNavItemClick = (item) => {
    console.log('Nav item clicked:', item);
  };
  const toggleDarkMode = () => {
    document.body.classList.toggle('dark-mode');
  };

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <a href="#" onClick={handleReload} className="company-logo">
            <img src="logo16-1.png" alt="Logo" />
          </a>

          <div className="nav-links" id="nav-links">
            <ul className="flex">
              <li><a href="../Weather-Forecast/main.html">Weather</a></li>
              <li><a href="../Add Note/index2.html">Add Note</a></li>
              <li 
                className="hover-link nav-item" 
                id="highlights" 
                onClick={() => handleNavItemClick('Highlights')}
              >
                Highlights
              </li>
              <li><a href="../bot/index.html">Bot</a></li>
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input 
                id="search-text" 
                type="text" 
                className="news-input" 
                placeholder="Enter.." 
                onKeyPress={handleKeyPress}
              />
              <button className="search-button" id="search-button">
                <img src="srch.png" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div className="toggle-button dark-mode-button" onClick={toggleDarkMode}>
                <div className="icons">
                  <FontAwesomeIcon icon={faMoon} />
                </div>
              </div>
              <div 
                id="user-info" 
                className="user-info" 
                style={{ marginTop: '2px', marginLeft: '8px', fontSize: '30px' }}
              >
                <a href="../login and signup/logout.php" className="logout-btn">
                  <div className="icons">
                    <FontAwesomeIcon icon={faSignOutAlt} style={{ color: 'cadetblue' }} />
                  </div>
                </a>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="scrollable-container">
          <div className="cards-container container flex" id="cards-container">
          </div>
        </div>
      </main>

      <div id="template_card" style={{ display: 'none' }}>
        <div className="card">
          <div className="card-header">
            <img src="400x200 dummy img.png" alt="News Image" id="news-img" />
          </div>
          <div className="card-content">
            <h3 id="news-title">This is the Title</h3>
            <h6 className="news-source">End Gadget 22/6/24</h6>
            <p className="news-des">
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Natus necessitatibus
              expedita debitis exercitationem aliquam iste in, nihil iusto consectetur? Quis laudantium impedit
              dolores mollitia alias.
            </p>
          </div>
        </div>
      </div>
    </>
  );
}

export default homePage;
