import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./style.css";
import '@fortawesome/fontawesome-free/css/all.min.css';

const API_KEY = "71146ebefc69425aa26e16e6e9e066d1";
const API_URL = "https://newsapi.org/v2/everything?q=";

const HomePage = () => {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [query, setQuery] = useState("");
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  useEffect(() => {
    fetchNews("India");
  }, []);

  const fetchNews = async (queryTerm) => {
    try {
      const res = await fetch(`${API_URL}${queryTerm}&apiKey=${API_KEY}&language=en`);
      const data = await res.json();
      if (data.articles.length === 0) {
        alert("No articles found");
      } else {
        setArticles(data.articles);
      }
    } catch (error) {
      alert("Error fetching news: " + error.message);
      console.error("Error fetching news:", error);
    }
  };

  const handleSearch = () => {
    if (!query.trim()) {
      alert("Please enter a valid search term");
      return;
    }
    fetchNews(query);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") handleSearch();
  };

  const reload = () => window.location.reload();

  const handleLogout = () => {
    // Optionally make an API call to a logout endpoint if your backend requires it
    // For now, we'll just redirect to the login page
    navigate("/");
  };

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <Link to="/NewsApp" onClick={reload} className="company-logo">
            <img src="logo16-1.png" alt="Logo" />
          </Link>

          <div className="nav-links" id="nav-links">
            <ul className="flex">
              <li><Link to="/weather">Weather</Link></li>
              <li><Link to="/add-note">Add Note</Link></li>
              <li className="hover-link nav-item" onClick={() => fetchNews("Highlights")}>Highlights</li>
              <li><Link to="/bot">Bot</Link></li>
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input
                id="search-text"
                type="text"
                className="news-input"
                placeholder="Enter.."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <button className="search-button" onClick={handleSearch}>
                <img src="srch.png" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div className="toggle-button dark-mode-button" onClick={() => setDarkMode(!darkMode)}>
                <div className="icons">
                  <i className="fas fa-moon"></i>
                </div>
              </div>
              <div id="user-info" className="user-info" style={{ marginTop: "2px", marginLeft: "8px", fontSize: "30px" }}>
                <button onClick={handleLogout} className="logout-btn" style={{ background: "none", border: "none", cursor: "pointer" }}>
                  <div className="icons">
                    <i className="fas fa-sign-out-alt" style={{ color: "cadetblue" }}></i>
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="scrollable-container">
          <div className="cards-container container flex" id="cards-container">
            {articles.map((article, index) => (
              article.urlToImage && (
                <div className="card" key={index} onClick={() => window.open(article.url, "_blank")}>
                  <div className="card-header">
                    <img src={article.urlToImage} alt="News" id="news-img" />
                  </div>
                  <div className="card-content">
                    <h3 id="news-title">{article.title}</h3>
                    <h6 className="news-source">
                      {article.source.name}{" "}
                      {new Date(article.publishedAt).toLocaleString("en-UK", {
                        timeZone: "Asia/Jakarta",
                      })}
                    </h6>
                    <p className="news-des">{article.description}</p>
                  </div>
                </div>
              )
            ))}
          </div>
        </div>
      </main>
    </>
  );
};

export default HomePage;
