import React, { useState, useEffect, useRef, useCallback } from 'react';
import './HomePage.css';
import logo from '../../assets/logo16-1.png';
import srch from '../../assets/srch.png';

const NewsCard = ({ article }) => {
  const date = new Date(article.publishedAt).toLocaleString("en-GB", {
    hour: '2-digit',
    minute: '2-digit',
    day: '2-digit',
    month: 'short',
    year: 'numeric'
  });

  return (
    <div className="card" onClick={() => window.open(article.url, "_blank")}>
      <div className="card-header">
        <img src={article.urlToImage} alt={article.title || "News Image"} id="news-img"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = `https://placehold.co/400x230/e0e0e0/505050?text=No+Image`;
          }}
        />
      </div>
      <div className="card-content">
        <h5 id="news-title">{article.title}</h5>
        <h6 className="news-source">{`${article.source.name} ${date}`}</h6>
        <p className="news-des">{article.description}</p>
      </div>
    </div>
  );
};

const HomePage = () => {
  const Api_key = "71146ebefc69425aa26e16e6e9e066d1";
  const url = "https://newsapi.org/v2/everything?";

  const [articles, setArticles] = useState([]);
  const [currentLanguage] = useState('en');
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const [currentNav, setCurrentNav] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const searchInputRef = useRef(null);

  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    fetchNews("India");
  }, []);

  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    document.body.style.background = darkMode ? 'rgb(14, 11, 11)' : 'white';
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]);

  const fetchNews = useCallback(async (query) => {
    setMessage('');
    setLoading(true);
    try {
      const fetchUrl = `${url}q=${query}&apiKey=${Api_key}&language=${currentLanguage}`;
      const res = await fetch(fetchUrl);

      if (!res.ok) throw new Error(res.statusText);

      const data = await res.json();
      const filtered = data.articles.filter(article => article.urlToImage);
      setArticles(filtered);
      if (!filtered.length) setMessage('No articles with images found. Try another search.');
    } catch (error) {
      setMessage(`Error fetching news: ${error.message}`);
      setArticles([]);
    } finally {
      setLoading(false);
    }
  }, [currentLanguage]);

  const onNavItemClick = useCallback((id) => {
    fetchNews(id);
    searchInputRef.current.value = '';
    setCurrentNav(id);
    setMenuOpen(false);
  }, [fetchNews]);

  const handleSearch = () => {
    const query = searchInputRef.current?.value.trim();
    if (!query) return setMessage('Please enter a valid search term.');
    fetchNews(query);
    setCurrentNav(null);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSearch();
  };

  const toggleDarkMode = () => {
    setDarkMode(prev => !prev);
  };

  const toggleMenu = () => {
    setMenuOpen(prev => !prev);
  };

  const reload = () => window.location.reload();

  return (
    <>
      <nav className="nav_top">
        <a href="#" onClick={reload} className="company-logo">
          <img src={logo} alt="Logo" />
        </a>

        <button className="hamburger" onClick={toggleMenu}>
          &#9776;
        </button>

        <div className={`nav-links ${menuOpen ? 'open' : ''}`}>
          <ul className="flex">
            <li><a href="../Weather-Forecast/main.html">Weather</a></li>
            <li><a href="../Add Note/index2.html">Add Note</a></li>
            <li className={`hover-link nav-item ${currentNav === 'highlights' ? 'active' : ''}`} onClick={() => onNavItemClick('highlights')}>Highlights</li>
            <li><a href="../bot/index.html">Bot</a></li>
          </ul>
        </div>

        <div className="assign">
          <div className="search-bar flex">
            <input
              id="search-text"
              type="text"
              className="news-input"
              placeholder="Enter.."
              onKeyPress={handleKeyPress}
              ref={searchInputRef}
            />
            <button className="search-button" id="search-button" onClick={handleSearch}>
              <img src={srch} alt="Search" />
            </button>
          </div>

          <div className='flex'>
            <div className="toggle-button dark-mode-button" onClick={toggleDarkMode}>
              <div className="icons">
                <i className="fas fa-moon"></i>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <div className="cards-container">
        {message && <div className="message">{message}</div>}
        {loading ? (
          <div className="loading">Loading news...</div>
        ) : articles.length > 0 ? (
          articles.map((article, index) => (
            <NewsCard key={article.url || index} article={article} />
          ))
        ) : (
          !message && <div className="no-news">No news found.</div>
        )}
      </div>
    </>
  );
};

export default HomePage;
