import React, { useState, useEffect, useRef } from "react";
import { Modal, Button, Form, Navbar, Nav, Container } from "react-bootstrap";
import "./style2.css";

const take_note = () => {
  const [globalStore, setGlobalStore] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showBlogModal, setShowBlogModal] = useState(false);
  const [currentBlog, setCurrentBlog] = useState(null);
  const imageUrlRef = useRef(null);
  const titleRef = useRef(null);
  const typeRef = useRef(null);
  const descriptionRef = useRef(null);

  useEffect(() => {
    loadData();
  }, []);

  const newCard = ({ id, imageUrl, blogTitle, blogType, blogDescription }) => (
    <div className="col-lg-4 col-md-6" id={id} key={id}>
      <div className="card m-2">
        <div className="card-header d-flex justify-content-end gap-2">
          <button
            type="button"
            className="btn btn-outline-success"
            id={id}
            onClick={(e) => editCard(e)}
          >
            <i className="fas fa-pencil-alt" id={id}></i>
          </button>
          <button
            type="button"
            className="btn btn-outline-danger"
            id={id}
            onClick={(e) => deleteCard(e)}
          >
            <i className="fas fa-trash-alt" id={id}></i>
          </button>
        </div>
        <img src={imageUrl} className="card-img-top" alt="..." />
        <div className="card-body">
          <h5 className="card-title">{blogTitle}</h5>
          <p className="card-text">{blogDescription}</p>
          <span className="badge bg-primary">{blogType}</span>
        </div>
        <div className="card-footer text-muted">
          <button
            type="button"
            id={id}
            className="btn btn-outline-primary float-end"
            onClick={(e) => openBlog(e)}
          >
            Open Blog
          </button>
        </div>
      </div>
    </div>
  );

  const loadData = () => {
    const getInitialData = localStorage.getItem("blog");
    if (!getInitialData) return;

    const { cards } = JSON.parse(getInitialData);
    setGlobalStore(cards);
  };

  const updateLocalStorage = () => {
    localStorage.setItem("blog", JSON.stringify({ cards: globalStore }));
  };

  const saveChanges = () => {
    const blogData = {
      id: `${Date.now()}`,
      imageUrl: imageUrlRef.current.value,
      blogTitle: titleRef.current.value,
      blogType: typeRef.current.value,
      blogDescription: descriptionRef.current.value,
    };

    setGlobalStore([...globalStore, blogData]);
    updateLocalStorage();
    setShowAddModal(false);

    // Clear form inputs
    imageUrlRef.current.value = "";
    titleRef.current.value = "";
    typeRef.current.value = "";
    descriptionRef.current.value = "";
  };

  const deleteCard = (event) => {
    const targetID = event.target.id;
    const tagname = event.target.tagName;

    const newGlobalStore = globalStore.filter(
      (blogObject) => blogObject.id !== targetID
    );
    setGlobalStore(newGlobalStore);
    updateLocalStorage();
  };

  const editCard = (event) => {
    const targetID = event.target.id;
    const tagname = event.target.tagName;

    const parentElement =
      tagname === "BUTTON"
        ? event.target.parentNode.parentNode
        : event.target.parentNode.parentNode.parentNode;

    const blogTitle = parentElement.querySelector(".card-title");
    const blogDescription = parentElement.querySelector(".card-text");
    const blogType = parentElement.querySelector(".badge");
    const submitBtn = parentElement.querySelector(".btn-outline-primary");

    blogTitle.setAttribute("contenteditable", "true");
    blogDescription.setAttribute("contenteditable", "true");
    blogType.setAttribute("contenteditable", "true");

    submitBtn.setAttribute("onClick", ""); // Remove existing onClick
    submitBtn.onclick = (e) => saveEditChanges(e);
    submitBtn.innerHTML = "Save Changes";

    submitBtn.removeAttribute("data-bs-toggle");
    submitBtn.removeAttribute("data-bs-target");
  };

  const saveEditChanges = (event) => {
    const targetID = event.target.id;
    const tagname = event.target.tagName;

    const parentElement =
      tagname === "BUTTON"
        ? event.target.parentNode.parentNode
        : event.target.parentNode.parentNode.parentNode;

    const blogTitle = parentElement.querySelector(".card-title");
    const blogDescription = parentElement.querySelector(".card-text");
    const blogType = parentElement.querySelector(".badge");
    const submitBtn = parentElement.querySelector(".btn-outline-primary");

    const updatedData = {
      blogTitle: blogTitle.innerHTML,
      blogDescription: blogDescription.innerHTML,
      blogType: blogType.innerHTML,
    };

    const newGlobalStore = globalStore.map((blog) => {
      if (blog.id === targetID) {
        return {
          id: blog.id,
          imageUrl: blog.imageUrl,
          blogTitle: updatedData.blogTitle,
          blogType: updatedData.blogType,
          blogDescription: updatedData.blogDescription,
        };
      }
      return blog;
    });

    setGlobalStore(newGlobalStore);
    updateLocalStorage();

    blogTitle.setAttribute("contenteditable", "false");
    blogDescription.setAttribute("contenteditable", "false");
    blogType.setAttribute("contenteditable", "false");

    submitBtn.onclick = (e) => openBlog(e);
    submitBtn.setAttribute("data-bs-toggle", "modal");
    submitBtn.setAttribute("data-bs-target", "#showblog");
    submitBtn.innerHTML = "Open Blog";
  };

  const htmlModalContent = ({ id, blogTitle, blogDescription, imageUrl, blogType }) => {
    const date = new Date(parseInt(id));
    return (
      <div id={id}>
        <img
          src={imageUrl}
          alt="bg image"
          className="img-fluid place__holder__image mb-3 p-4"
        />
        <div className="text-sm text-muted">Created on {date.toDateString()}</div>
        <h2 className="my-5 mt-5" style={{ display: "inline" }}>
          {blogTitle}
        </h2>
        <span className="badge bg-primary">{blogType}</span>
        <p className="lead mt-2">{blogDescription}</p>
      </div>
    );
  };

  const openBlog = (event) => {
    const targetID = event.target.id;
    const getBlog = globalStore.find(({ id }) => id === targetID);
    if (getBlog) {
      setCurrentBlog(getBlog);
      setShowBlogModal(true);
    }
  };

  return (
    <>
      <head>
        <meta charset="UTF-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <link
          rel="preconnect"
          href="https://fonts.gstatic.com"
          crossOrigin="anonymous"
        />
        <link
          href="https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap"
          rel="stylesheet"
        />
        <link
          rel="stylesheet"
          href="https://use.fontawesome.com/releases/v5.15.3/css/all.css"
          integrity="sha384-SZXxX4whJ79/gErwcOYf+zWLeJdY/qpuqC4cAa9rOGUstPomtqpuNWT9wdPEn2fk"
          crossOrigin="anonymous"
        />
        <link
          href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.1/dist/css/bootstrap.min.css"
          rel="stylesheet"
          integrity="sha384-+0n0xVW2eSR5OomGNYDnhzAbDsOXxcvSN1TPprVMTNDbiYZCxYbOOl7+AMvyTG2x"
          crossOrigin="anonymous"
        />
        <title>Blog App</title>
      </head>
      <body>
        {/* Add New Blog Modal */}
        <Modal
          show={showAddModal}
          onHide={() => setShowAddModal(false)}
          backdrop="static"
          keyboard={false}
          id="staticBackdrop"
          aria-labelledby="staticBackdropLabel"
        >
          <Modal.Header closeButton>
            <Modal.Title id="staticBackdropLabel">Add New Task</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="imageurl">Image URL</Form.Label>
                <Form.Control
                  type="url"
                  id="imageurl"
                  placeholder="https://images.hello.com/hello.png"
                  ref={imageUrlRef}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="title">Title</Form.Label>
                <Form.Control
                  type="text"
                  id="title"
                  placeholder="Artificial Intelligence"
                  ref={titleRef}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="type">Type</Form.Label>
                <Form.Control
                  type="text"
                  id="type"
                  placeholder="AI"
                  ref={typeRef}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label htmlFor="description">Description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  id="description"
                  ref={descriptionRef}
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowAddModal(false)}>
              Close
            </Button>
            <Button variant="primary" onClick={saveChanges}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>

        {/* Show Blog Modal */}
        <Modal
          show={showBlogModal}
          onHide={() => setShowBlogModal(false)}
          id="showblog"
          size="lg"
          aria-labelledby="showTaskLabel"
        >
          <Modal.Body className="blog__modal__body">
            {currentBlog && htmlModalContent(currentBlog)}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowBlogModal(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        {/* Navbar */}
        <Navbar expand="md" bg="light" variant="light" className="shadow-sm">
          <Container fluid>
            <Navbar.Brand href="#" className="fw-bold text-primary">
              Blog
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="navbarSupportedContent" />
            <Navbar.Collapse id="navbarSupportedContent">
              <Nav className="me-auto mb-2 mb-lg-0">
                <Nav.Link href="#" active>
                  Home
                </Nav.Link>
              </Nav>
              <Button
                variant="primary"
                className="rounded-pill"
                onClick={() => setShowAddModal(true)}
              >
                <i className="fas fa-plus"></i> Add New
              </Button>
            </Navbar.Collapse>
          </Container>
        </Navbar>

        {/* Blog Container */}
        <Container>
          <section>
            <div className="row blog__container mt-5 mb-3">
              {globalStore.map((blogObject) => newCard(blogObject))}
            </div>
          </section>
        </Container>

        {/* Bootstrap Scripts */}
        <script
          src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.9.2/dist/umd/popper.min.js"
          integrity="sha384-IQsoLXl5PILFhosVNubq5LC7Qb9DXgDA9i+tQ8Zj3iwWAwPtgFTxbJ8NT4GN1R8p"
          crossOrigin="anonymous"
        ></script>
        <script
          src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.1/dist/js/bootstrap.min.js"
          integrity="sha384-Atwg2Pkwv9vp0ygtn1JAojH0nYbwNJLPhwyoVbhoPwBhjQPR5VtM2+xf0Uwh9KtT"
          crossOrigin="anonymous"
        ></script>
      </body>
    </>
  );
};

export default take_note;
