import { useState } from "react";
import { useNavigate } from "react-router-dom";
import '@fortawesome/fontawesome-free/css/all.min.css'; // Import Font Awesome
import './LoginPage.css';
import home from "./home/HomePage";

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (email.length === 0) {
      alert("Email cannot be blank!");
      return;
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      alert("Please enter a valid email address!");
      return;
    }
    if (password.length === 0) {
      alert("Password cannot be blank!");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password
        })
      });

      if (response.ok) {
        // navigate("/home/HomePage");
        <Route path="/home" component={Chat} />
      } else if (response.status === 401) {
        alert("Invalid credentials");
      } else {
        alert("An error occurred. Please try again.");
      }
    } catch (error) {
      console.error('Login error:', error);
      alert("Failed to connect to the server. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h2 className="title">Log Into Your Account</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <i className="fas fa-envelope"></i>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              id="form3Example3"
              className="input-field"
              placeholder=" "
            />
            <label htmlFor="form3Example3" className="input-label">
              Email
            </label>
          </div>

          <div className="input-group">
            <i className="fas fa-lock"></i>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              id="form3Example4"
              className="input-field"
              placeholder=" "
            />
            <label htmlFor="form3Example4" className="input-label">
              Password
            </label>
          </div>

          <div className="checkbox-group">
            <div className="checkbox-wrapper">
              <input type="checkbox" id="form2Example3" className="checkbox" />
              <label htmlFor="form2Example3" className="checkbox-label">
                Remember me
              </label>
            </div>
            <a href="#!" className="forgot-link">
              Forgot password?
            </a>
          </div>

          <button type="submit" className="login-btn" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <p className="register-text">
          Don't have an account?{' '}
          <a href="/register" className="register-link">
            Register
          </a>
          {/* TODO: Ensure /register route is defined in App.js or points to a valid registration page */}
        </p>
      </div>
    </div>
  );
}
