import React, { useState, useEffect, useCallback } from 'react';
import Navbar from './Navbar'; 
import '../WeatherApp.css'; 

// Helper function to format date/time for forecast (Day, Mon)
const formatForecastDay = (dt_txt, timezoneOffset) => {
    // dt_txt is in UTC, timezoneOffset is in seconds
    const date = new Date((new Date(dt_txt).getTime() + timezoneOffset * 1000));
    const options = { weekday: 'short', month: 'short', day: 'numeric' };
    return date.toLocaleDateString('en-US', options).replace(/, \d{4}/, ''); // Remove year
};

const WeatherApp = () => {
    const OPENWEATHER_API_KEY = "93777aa63aa67084e1bd09488ec46331"; 
    const [city, setCity] = useState('Durgapur');
    const [weatherData, setWeatherData] = useState(null);
    const [forecastData, setForecastData] = useState([]); 
    const [error, setError] = useState(null);
    const [searchCity, setSearchCity] = useState('');
    const [loading, setLoading] = useState(false);

    const fetchWeatherAndForecast = useCallback(async (cityName) => {
        setLoading(true);
        setError(null); 
        try {
            const weatherResponse = await fetch(
                `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&units=metric&appid=${OPENWEATHER_API_KEY}`
            );

            if (!weatherResponse.ok) {
                if (weatherResponse.status === 404) {
                    throw new Error('City not found. Please enter a valid city name.');
                }
                throw new Error(`Error fetching current weather: ${weatherResponse.statusText}`);
            }
            const currentWeatherData = await weatherResponse.json();
            setWeatherData(currentWeatherData);


            const forecastResponse = await fetch(
                `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&units=metric&appid=${OPENWEATHER_API_KEY}`
            );

            if (!forecastResponse.ok) {
                throw new Error(`Error fetching forecast: ${forecastResponse.statusText}`);
            }
            const fiveDayForecastData = await forecastResponse.json();

            // Filter forecast data to get one entry per day for the next 5 days
            const dailyForecastsMap = new Map(); // Use a Map to store unique days
            const today = new Date((currentWeatherData.dt + currentWeatherData.timezone) * 1000)
                            .toISOString().split('T')[0]; // Get today's date in local time

            if (fiveDayForecastData.list) {
                fiveDayForecastData.list.forEach(item => {
                    const itemDate = new Date((item.dt + currentWeatherData.timezone) * 1000);
                    const dayKey = itemDate.toISOString().split('T')[0]; // YYYY-MM-DD

                    if (dayKey !== today) {
                        const localHour = itemDate.getUTCHours();
                        if (!dailyForecastsMap.has(dayKey) && localHour >= 10 && localHour <= 16) {
                            dailyForecastsMap.set(dayKey, item);
                        }
                    }
                });
            }

            fiveDayForecastData.list.forEach(item => {
                const itemDate = new Date((item.dt + currentWeatherData.timezone) * 1000);
                const dayKey = itemDate.toISOString().split('T')[0];
                if (dayKey !== today && !dailyForecastsMap.has(dayKey)) {
                    dailyForecastsMap.set(dayKey, item);
                }
            });

            const sortedForecast = Array.from(dailyForecastsMap.values())
                                        .sort((a, b) => a.dt - b.dt)
                                        .slice(0, 5); // Get exactly the next 5 days

            setForecastData(sortedForecast);

        } catch (err) {
            console.error('Error fetching weather data:', err);
            setError(err.message || 'Failed to fetch weather data. Please try again.');
            setWeatherData(null);
            setForecastData([]);
        } finally {
            setLoading(false);
        }
    }, [OPENWEATHER_API_KEY]);

    useEffect(() => {
        fetchWeatherAndForecast(city);
    }, [city, fetchWeatherAndForecast]);

    const handleSearch = (e) => {
        e.preventDefault();
        if (searchCity.trim()) {
            setCity(searchCity.trim());
            setSearchCity('');
        }
    };

    const formatDate = (timestamp, timezoneOffset) => {
        const date = new Date((timestamp + timezoneOffset) * 1000);
        const options = { weekday: 'long', month: 'long', day: 'numeric' };
        return date.toLocaleDateString(undefined, options);
    };

    const getIconUrl = (iconCode) => `http://openweathermap.org/img/wn/${iconCode}@2x.png`;

    if (loading) {
        return (
            <div className="weather-app">
                <Navbar />
                <div className="loading-container">
                    <p>Loading weather data...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="weather-app">
            <Navbar />
            <div className="containerEl">
                <h3 className="city">Weather in City</h3>
                {weatherData ? (
                    <>
                        <span className="temp">{weatherData.main.temp.toFixed(1)}</span><span className="temp">&#176;</span>
                        <div className="city-time">
                            <h1 className="name" id="cityName">{weatherData.name} âš²</h1>
                            <small>
                                <span className="data">{formatDate(weatherData.dt, weatherData.timezone)}</span>
                            </small>
                        </div>
                        <div className="weather">
                            {weatherData.weather && weatherData.weather[0] && (
                                <img
                                    src={getIconUrl(weatherData.weather[0].icon)}
                                    className="icon"
                                    alt={weatherData.weather[0].description}
                                    width="50"
                                    height="50"
                                />
                            )}
                            <span className="condition">{weatherData.weather[0].main}</span>
                        </div>
                    </>
                ) : (
                    // Display error if no weatherData
                    <p className="error-message">{error || 'No weather data available.'}</p>
                )}
            </div>
            <div className="panel">
                <form id="locationInput" onSubmit={handleSearch}>
                    <input
                        type="text"
                        className="search"
                        id="searchBar"
                        placeholder="Search location.."
                        value={searchCity}
                        onChange={(e) => setSearchCity(e.target.value)}
                    />
                    <button type="submit" className="submit" id="searchBtn">
                        <span className="material-symbols-outlined" id="search_icon">search</span>
                    </button>
                </form>

                <ul className="details">
                    <h4>Weather details</h4>
                    <li>
                        <span>Humidity</span>
                        <span className="humidity" id="humidity">{weatherData?.main?.humidity}%</span>
                    </li>
                    <li>
                        <span>Wind Speed</span>
                        <span className="wind" id="wind">{weatherData?.wind?.speed ? (weatherData.wind.speed * 3.6).toFixed(1) : 'N/A'} km/h</span>
                    </li>
                    <li>
                        <span>Pressure</span>
                        <span className="cloud" id="pressure">{weatherData?.main?.pressure} millibar</span>
                    </li>
                </ul>

                {forecastData.length > 0 && (
                    <div className="forecast-list-section">
                        <h4>5-Day Forecast</h4>
                        <ul className="forecast-list-details">
                            {forecastData.map((forecastItem, index) => (
                                <li key={index} className="forecast-list-item">
                                    <span className="forecast-list-day">{formatForecastDay(forecastItem.dt_txt, weatherData.timezone)}</span>
                                    <img
                                        src={`http://openweathermap.org/img/wn/${forecastItem.weather[0].icon}.png`}
                                        alt={forecastItem.weather[0].description}
                                        width="30"
                                        height="30"
                                    />
                                    <span className="forecast-list-temp">{Math.round(forecastItem.main.temp)}&#176;C</span>
                                    <span className="forecast-list-condition">{forecastItem.weather[0].main}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
                {/* Display general error message if there's an error and no weather data */}
                {error && !weatherData && <p className="error-message" style={{ color: 'red', marginTop: '20px' }}>{error}</p>}
            </div>
        </div>
    );
};

export default WeatherApp;
