import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMoon, faSun, faSignOutAlt } from "@fortawesome/free-solid-svg-icons";
import "./style.css";

const API_KEY = "71146ebefc69425aa26e16e6e9e066d1"; // Replace with your key
const API_URL = "https://newsapi.org/v2/everything?q=";

const NewsApp = () => {
  const [articles, setArticles] = useState([]);
  const [query, setQuery] = useState("India");
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [currentTime, setCurrentTime] = useState("");
  const searchRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchNews(query);
  }, [query]);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  useEffect(() => {
    const updateDateTime = () => {
      const now = new Date();
      const options = {
        timeZone: "Asia/Kolkata",
        weekday: "long",
        year: "numeric",
        month: "long",
        day: "numeric",
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      };
      const formatted = now.toLocaleString("en-US", options).split(", ");
      const [weekday, monthDayYear, time] = formatted;
      setCurrentTime(`${time} IST on ${weekday}, ${monthDayYear}`);
    };

    updateDateTime();
    const interval = setInterval(updateDateTime, 60000);
    return () => clearInterval(interval);
  }, []);

  const fetchNews = async (q) => {
    try {
      const res = await fetch(`${API_URL}${q}&apiKey=${API_KEY}&language=en`);
      const data = await res.json();
      if (data.articles?.length) {
        setArticles(data.articles.filter((a) => a.urlToImage));
      } else {
        alert("No articles found.");
        setArticles([]);
      }
    } catch (err) {
      console.error("Fetch error:", err);
      alert("Error fetching news.");
    }
  };

  const handleSearch = () => {
    const value = searchRef.current?.value?.trim();
    if (value) setQuery(value);
    else alert("Please enter a valid search term");
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") handleSearch();
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <Link to="/NewsApp" className="company-logo" onClick={() => setQuery("India")}>
            <img src="/logo16-1.png" alt="Logo" />
          </Link>

          <div className="nav-links" id="nav-links">
            <ul className="flex">
              <li><Link to="/Weather-Forecast/weather">Weather</Link></li>
              <li><Link to="/Add-Note/take_note">Add Note</Link></li>
              <li className="hover-link nav-item" onClick={() => setQuery("Highlights")}>Highlights</li>
              <li><Link to="/bot/bot_ai">Bot</Link></li>
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input
                ref={searchRef}
                type="text"
                className="news-input"
                placeholder="Enter..."
                onKeyPress={handleKeyPress}
              />
              <button className="search-button" onClick={handleSearch}>
                <img src="/srch.png" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div
                className="toggle-button dark-mode-button"
                onClick={() => setDarkMode((prev) => !prev)}
              >
                <div className="icons">
                  <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
                </div>
              </div>

              <div className="user-info" style={{ marginTop: "2px", marginLeft: "8px", fontSize: "30px" }}>
                <button className="logout-btn" onClick={handleLogout}>
                  <div className="icons">
                    <FontAwesomeIcon icon={faSignOutAlt} style={{ color: "cadetblue" }} />
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="container">
          <div className="date-time" style={{ textAlign: "center", margin: "20px 0", fontSize: "1.2rem", color: darkMode ? "#fff" : "#333" }}>
            {currentTime || "Loading date and time..."}
          </div>
          <div className="scrollable-container">
            <div className="cards-container container flex">
              {articles.map((article, idx) => (
                <div className="card" key={idx} onClick={() => window.open(article.url, "_blank")}>
                  <div className="card-header">
                    <img src={article.urlToImage} alt="News" />
                  </div>
                  <div className="card-content">
                    <h3>{article.title}</h3>
                    <h6 className="news-source">
                      {article.source.name}{" "}
                      {new Date(article.publishedAt).toLocaleString("en-UK", {
                        timeZone: "Asia/Jakarta",
                      })}
                    </h6>
                    <p className="news-des">{article.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </main>
    </>
  );
};

export default NewsApp;
