import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import '@fortawesome/fontawesome-free/css/all.min.css';


export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const navigate = useNavigate();

  const logInUser = () => {
    if (email.length === 0) {
      alert("Email has left Blank!");
    } else if (password.length === 0) {
      alert("password has left Blank!");
    } else {
      axios.post('http://127.0.0.1:5000/login', {
        email: email,
        password: password
      })
        .then(function (response) {
          console.log(response);
          navigate("/");
        })
        .catch(function (error) {
          console.log(error, 'error');
          if (error.response.status === 401) {
            alert("Invalid credentials");
          }
        });
    }
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#f0f2f5'
      }}
    >
      <div
        style={{
          backgroundColor: '#fff',
          padding: '2rem',
          borderRadius: '15px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
          width: '100%',
          maxWidth: '400px'
        }}
      >
        <h2
          style={{
            fontSize: '1.5rem',
            fontWeight: 'bold',
            textAlign: 'center',
            marginBottom: '1.5rem',
            color: '#333'
          }}
        >
          Log Into Your Account
        </h2>

        {/* Email Input */}
        <div style={{ position: 'relative', marginBottom: '1.5rem' }}>
          <i
            className="bi bi-envelope"
            style={{
              position: 'absolute',
              left: '0',
              top: '50%',
              transform: 'translateY(-50%)',
              color: '#555',
              fontSize: '1.2rem'
            }}
          ></i>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            id="form3Example3"
            style={{
              width: '100%',
              padding: '0.5rem 0 0.5rem 2rem',
              border: 'none',
              borderBottom: '1px solid #ccc',
              fontSize: '1rem',
              color: '#333',
              outline: 'none'
            }}
            onFocus={(e) => {
              e.target.style.borderBottom = '1px solid #6f42c1';
              document.getElementById('email-label').style.top = '-0.5rem';
              document.getElementById('email-label').style.fontSize = '0.8rem';
              document.getElementById('email-label').style.color = '#6f42c1';
            }}
            onBlur={(e) => {
              e.target.style.borderBottom = '1px solid #ccc';
              if (!email) {
                document.getElementById('email-label').style.top = '50%';
                document.getElementById('email-label').style.fontSize = '1rem';
                document.getElementById('email-label').style.color = '#555';
              }
            }}
          />
          <label
            id="email-label"
            htmlFor="form3Example3"
            style={{
              position: 'absolute',
              left: '2rem',
              top: '50%',
              transform: 'translateY(-50%)',
              color: '#555',
              fontSize: '1rem',
              transition: 'all 0.3s ease',
              pointerEvents: 'none'
            }}
          >
            Email
          </label>
        </div>

        {/* Password Input */}
        <div style={{ position: 'relative', marginBottom: '1.5rem' }}>
          <i
            className="bi bi-lock"
            style={{
              position: 'absolute',
              left: '0',
              top: '50%',
              transform: 'translateY(-50%)',
              color: '#555',
              fontSize: '1.2rem'
            }}
          ></i>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            id="form3Example4"
            style={{
              width: '100%',
              padding: '0.5rem 0 0.5rem 2rem',
              border: 'none',
              borderBottom: '1px solid #ccc',
              fontSize: '1rem',
              color: '#333',
              outline: 'none'
            }}
            onFocus={(e) => {
              e.target.style.borderBottom = '1px solid #6f42c1';
              document.getElementById('password-label').style.top = '-0.5rem';
              document.getElementById('password-label').style.fontSize = '0.8rem';
              document.getElementById('password-label').style.color = '#6f42c1';
            }}
            onBlur={(e) => {
              e.target.style.borderBottom = '1px solid #ccc';
              if (!password) {
                document.getElementById('password-label').style.top = '50%';
                document.getElementById('password-label').style.fontSize = '1rem';
                document.getElementById('password-label').style.color = '#555';
              }
            }}
          />
          <label
            id="password-label"
            htmlFor="form3Example4"
            style={{
              position: 'absolute',
              left: '2rem',
              top: '50%',
              transform: 'translateY(-50%)',
              color: '#555',
              fontSize: '1rem',
              transition: 'all 0.3s ease',
              pointerEvents: 'none'
            }}
          >
            Password
          </label>
        </div>

        {/* Remember Me and Forgot Password */}
        <div
          style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '1.5rem'
          }}
        >
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <input
              type="checkbox"
              id="form2Example3"
              style={{ marginRight: '0.5rem' }}
            />
            <label
              htmlFor="form2Example3"
              style={{ fontSize: '0.9rem', color: '#555' }}
            >
              Remember me
            </label>
          </div>
          <a
            href="#!"
            style={{
              fontSize: '0.9rem',
              color: '#6f42c1',
              textDecoration: 'none'
            }}
          >
            Forgot password?
          </a>
        </div>

        {/* Login Button */}
        <button
          type="button"
          onClick={logInUser}
          style={{
            width: '100%',
            backgroundColor: '#6f42c1',
            color: '#fff',
            padding: '0.75rem',
            border: 'none',
            borderRadius: '10px',
            fontSize: '1rem',
            fontWeight: '500',
            cursor: 'pointer',
            transition: 'background-color 0.3s ease'
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = '#563d7c')}
          onMouseOut={(e) => (e.target.style.backgroundColor = '#6f42c1')}
        >
          Login
        </button>

        {/* Register Link */}
        <p
          style={{
            textAlign: 'center',
            marginTop: '1rem',
            fontSize: '0.9rem',
            color: '#555'
          }}
        >
          Don't have an account?{' '}
          <a
            href="/register"
            style={{ color: '#6f42c1', textDecoration: 'none' }}
          >
            Register
          </a>
        </p>
      </div>
    </div>
  );
}
