import React, { useState, useEffect, useRef, useCallback } from 'react';
import './style.css'; 
import logo from '../assets/logo16-1.png';

// Then in your JSX:
<img src={logo} alt="News Logo" className="logo" />

const NewsCard = ({ article }) => {
  const date = new Date(article.publishedAt).toLocaleString("en-GB", {
    hour: '2-digit',
    minute: '2-digit',
    day: '2-digit',
    month: 'short',
    year: 'numeric'
  });

  return (
    <div className="card" onClick={() => window.open(article.url, "_blank")}>
      <div className="card-header">
        <img src={article.urlToImage} alt={article.title || "News Image"} id="news-img"
          onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/400x230/e0e0e0/505050?text=No+Image`; }}
        />
      </div>
      <div className="card-content">
        <h3 id="news-title">{article.title}</h3>
        <h6 className="news-source">{`${article.source.name} ${date}`}</h6>
        <p className="news-des">{article.description}</p>
      </div>
    </div>
  );
};

const HomePage = () => {
  // IMPORTANT: Ensure this API_KEY is valid and not expired/rate-limited.
  // You should get your own API key from https://newsapi.org/
  const Api_key = "71146ebefc69425aa26e16e6e9e066d1";
  const url = "https://newsapi.org/v2/everything?"; // Changed to end with '?' for query params

  const [articles, setArticles] = useState([]);
  const [currentLanguage] = useState('en');
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const [currentNav, setCurrentNav] = useState(null);
  const [message, setMessage] = useState(''); // For user feedback messages
  const [loading, setLoading] = useState(false); // New loading state

  const searchInputRef = useRef(null);

  // Effect to load initial news and apply dark mode class on body
  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    fetchNews("India");
  }, []);

  // Effect to update dark mode preference in localStorage and apply to body
  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]);

  // Function to fetch news articles from NewsAPI
  const fetchNews = useCallback(async (query) => {
    setMessage('');
    setLoading(true);
    try {
      // Construct URL with API key as a query parameter
      const fetchUrl = `${url}q=${query}&apiKey=${Api_key}&language=${currentLanguage}`;

      const res = await fetch(fetchUrl, {
        headers: {
          'User-Agent': 'News-App/1.0' // NewsAPI often requires a User-Agent header
        }
      });

      if (!res.ok) {
        if (res.status === 426) {
            setMessage('Error: Your NewsAPI key might be invalid or have exceeded its usage limits. Please check your key on NewsAPI.org.');
        } else {
            setMessage(`Error fetching news: ${res.statusText || 'Unknown Error'} (Status: ${res.status}). Please check your internet connection or try again later.`);
        }
        throw new Error(`Network response was not ok: ${res.statusText} (Status: ${res.status})`);
      }

      const data = await res.json();
      if (data.articles && data.articles.length > 0) {
        const articlesWithImages = data.articles.filter(article => article.urlToImage);
        setArticles(articlesWithImages);
        if (articlesWithImages.length === 0) {
            setMessage('No articles with images found for your query. Try a different search term.');
        }
      } else {
        setArticles([]);
        setMessage('No articles found for your query. Try a different search term.');
      }
    } catch (error) {
      console.error('Error fetching news:', error);
      if (!message.includes("API key")) { // Avoid overwriting specific API key error message
        setMessage(`Error fetching news: ${error.message}. Please check your internet connection or try again later.`);
      }
      setArticles([]);
    } finally {
      setLoading(false);
    }
  }, [currentLanguage, message]);

  const onNavItemClick = useCallback((id) => {
    fetchNews(id);
    if (searchInputRef.current) {
      searchInputRef.current.value = '';
    }
    setCurrentNav(id);
  }, [fetchNews]);

  const handleSearch = useCallback(() => {
    const query = searchInputRef.current?.value.trim();
    if (!query) {
      setMessage('Please enter a valid search term.');
      return;
    }
    fetchNews(query);
    setCurrentNav(null);
  }, [fetchNews]);

  const handleKeyPress = useCallback((event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  }, [handleSearch]);

  const toggleDarkMode = useCallback(() => {
    setDarkMode(prevMode => !prevMode);
  }, []);

  const reload = useCallback(() => {
    window.location.reload();
  }, []);

  return (
    <>
      <nav>
        <div className="main-nav container flex">
          <a href="#" onClick={reload} className="company-logo">
            <img src=".Home/logo16-1.png" alt="Logo" />
          </a>

          <div className="nav-links" id="nav-links">
            <ul className="flex">
              <li><a href="../Weather-Forecast/main.html">Weather</a></li>
              <li><a href="../Add Note/index2.html">Add Note</a></li>
              <li
                className={`hover-link nav-item ${currentNav === 'highlights' ? 'active' : ''}`}
                id="highlights"
                onClick={() => onNavItemClick('highlights')}
              >
                Highlights
              </li>
              <li><a href="../bot/index.html">Bot</a></li>
            </ul>
          </div>

          <div className="assign flex">
            <div className="search-bar flex">
              <input
                id="search-text"
                type="text"
                className="news-input"
                placeholder="Enter.."
                onKeyPress={handleKeyPress}
                ref={searchInputRef}
              />
              <button className="search-button" id="search-button" onClick={handleSearch}>
                <img src="https://placehold.co/30x20/FFFFFF/000000?text=🔎" alt="Search" />
              </button>
            </div>

            <div className="assign flex">
              <div className="toggle-button dark-mode-button" onClick={toggleDarkMode}>
                <div className="icons">
                  <i className="fas fa-moon"></i>
                </div>
              </div>
              <div id="user-info" className="user-info" style={{ marginTop: '2px', marginLeft: '8px', fontSize: '30px' }}>
                <a href="../login and signup/logout.php" className="logout-btn">
                  <div className="icons">
                    <i className="fas fa-sign-out-alt" style={{ color: 'cadetblue' }}></i>
                  </div>
                </a>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <main>
        <div className="scrollable-container">
          <div className="cards-container container flex" id="cards-container">
            {message && (
              <div className="col-span-full w-full bg-blue-100 border border-blue-400 text-blue-700 px-4 py-3 rounded relative mb-4">
                <span className="block sm:inline">{message}</span>
              </div>
            )}
            {loading ? (
                <p className="col-span-full text-center text-lg text-gray-600">Loading news, please wait...</p>
            ) : articles.length > 0 ? (
              articles.map((article, index) => (
                <NewsCard key={article.url || index} article={article} />
              ))
            ) : (
              !message && <p className="col-span-full text-center text-lg text-gray-600">No news articles found. Please try a different search or check your API key.</p>
            )}
          </div>
        </div>
      </main>

      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css" xintegrity="sha512-1ycn6IcaQQ40/MKBW2W4Rhis/DbILU74C1vSrLJxCq57o941Ym01SwNsOMqvEBFlcgUa6xLiPY/NS5R+E6ztJQ==" crossOrigin="anonymous" referrerPolicy="no-referrer" />
      <script src="https://cdn.tailwindcss.com"></script>
    </>
  );
};

export default HomePage;
