import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import './RegisterPage.css'; // Import the new CSS

export default function RegisterPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const signUpUser = () => {
        if (email.length === 0) {
            alert("Email has left Blank!");
        } else if (password.length === 0) {
            alert("Password has left Blank!");
        } else {
            axios.post('http://127.0.0.1:5000/signup', {
                email: email,
                password: password
            })
            .then(function (response) {
                console.log(response);
                navigate("/login");
            })
            .catch(function (error) {
                console.log(error, 'error');
                if (error.response.status === 409) {
                    alert("Email already exists");
                } else {
                    alert("Signup failed. Please try again.");
                }
            });
        }
    };

    return (
        <div>
            <div className="register-container">
                <div className="register-card">
                    <form>
                        <div className="title-container">
                            <p className="title">Create Your Account</p>
                        </div>

                        <div className="input-group">
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                id="form3Example3"
                                className="input-field"
                                placeholder="Enter a valid email address"
                            />
                            <label className="input-label" htmlFor="form3Example3">Email address</label>
                        </div>

                        <div className="input-group">
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                id="form3Example4"
                                className="input-field"
                                placeholder="Enter password"
                            />
                            <label className="input-label" htmlFor="form3Example4">Password</label>
                        </div>

                        <div className="button-container">
                            <button type="button" className="signup-button" onClick={signUpUser}>Sign Up</button>
                            <p className="login-text">
                                Already have an account? <a href="/login" className="login-link">Login</a>
                            </p>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}
