    <div className="app-container">
      <nav>
        {/* Keep the existing nav code */}
      </nav>

      <main>
        {loading && <div className="loading-message">Loading news...</div>}
        {error && <div className="error-message">{error}</div>}

        {!loading && !error && (
          <div className="cards-container">
            {articles.map((article, index) => (
              <div className="card" key={`${article.url}-${index}`}>
                <div className="card-header">
                  <img
                    src={article.urlToImage || 'https://placehold.co/300x200?text=No+Image'}
                    alt={article.title}
                  />
                </div>
                <div className="card-content">
                  <h3>{article.title}</h3>
                  <p className="news-source">
                    {article.source?.name || 'Unknown'} â€¢ {new Date(article.publishedAt).toLocaleDateString()}
                  </p>
                  <p className="news-description">
                    {article.description?.substring(0, 100)}...
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
};

export default HomePage;
