import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import "./CSS/style1.css";
import "./CSS/style2.css";
import Swal from "sweetalert";

const weatherApi = {
  key: "5174a4c980abc22f0dc589db984742cf",
  baseUrl: "https://api.openweathermap.org/data/2.5/weather",
};

// Introductory page component (replaces index.html)
const weather = () => {
  return (
    <div className="outer-bg over-lay">
      <div className="bg-img"></div>
      <div className="centered-box inner-bg">
        <img src="./images/_berenice_.gif" className="logo" alt="Weather logo" />
      </div>
      <div className="content">
        <h2>
          Weather<br />Forecasts
        </h2>
      </div>
      <Link to="/weather">
        <button id="btn" className="btn">
          Get Started
        </button>
      </Link>
      <p className="para">Check any weather</p>
    </div>
  );
};

// Main weather page component (replaces main.html)
const WeatherMain = () => {
  const [weatherData, setWeatherData] = useState(null);
  const searchInputBox = useRef(null);
  const weatherBodyRef = useRef(null);

  useEffect(() => {
    // Initially hide the weather body
    if (weatherBodyRef.current) {
      weatherBodyRef.current.style.display = "none";
    }
  }, []);

  const getWeatherReport = (city) => {
    if (city.trim() === "") {
      Swal("Enter Input", "Please enter a city name", "warning");
      if (weatherBodyRef.current) {
        weatherBodyRef.current.style.display = "none";
      }
      return;
    }

    fetch(`${weatherApi.baseUrl}?q=${city}&appid=${weatherApi.key}&units=metric`)
      .then((weather) => weather.json())
      .then(showWeaterReport)
      .catch((error) => {
        console.error("Error fetching weather:", error);
        Swal("Error", "Failed to fetch weather data", "error");
        reset();
      });
  };

  const showWeaterReport = (weather) => {
    let city_code = weather.cod;
    if (city_code === "400") {
      Swal("Empty Input", "Please enter any city", "error");
      reset();
    } else if (city_code === "404") {
      Swal("Enter Valid Input", "Warning..!!");
      reset();
    } else {
      setWeatherData(weather);
      if (weatherBodyRef.current) {
        weatherBodyRef.current.style.display = "block";
      }
      changeBg(weather.weather[0].main);
      reset();
    }
  };

  const getTime = (todayDate) => {
    let hour = addZero(todayDate.getHours());
    let minute = addZero(todayDate.getMinutes());
    return `${hour}:${minute}`;
  };

  const dateManage = (dateArg) => {
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let year = dateArg.getFullYear();
    let month = months[dateArg.getMonth()];
    let date = dateArg.getDate();
    let day = days[dateArg.getDay()];
    return `${date} ${month} (${day}) , ${year}`;
  };

  const changeBg = (status) => {
    if (status === "Clouds") {
      document.body.style.backgroundImage = "url(../images/clouds.jpeg)";
    } else if (status === "Rain") {
      document.body.style.backgroundImage = "url(../images/rain.jpeg)";
    } else if (status === "Clear") {
      document.body.style.backgroundImage = "url(../images/clear.jpeg)";
    } else if (status === "Snow") {
      document.body.style.backgroundImage = "url(../images/snow.jpeg)";
    } else if (status === "Sunny") {
      document.body.style.backgroundImage = "url(../images/sunny.jpeg)";
    } else if (status === "Thunderstorm") {
      document.body.style.backgroundImage = "url(../images/thunder.jpeg)";
    } else if (status === "Drizzle") {
      document.body.style.backgroundImage = "url(../images/drizzle.jpeg)";
    } else if (status === "Mist" || status === "Haze" || status === "Fog") {
      document.body.style.backgroundImage = "url(../images/mist.jpeg)";
    } else {
      document.body.style.backgroundImage = "url(../images/bg1.jpeg)";
    }
  };

  const getIconClass = (classarg) => {
    if (classarg === "Rain") {
      return "fas fa-cloud-showers-heavy";
    } else if (classarg === "Clouds") {
      return "fas fa-cloud";
    } else if (classarg === "Clear") {
      return "fas fa-cloud-sun";
    } else if (classarg === "Snow") {
      return "fas fa-snowman";
    } else if (classarg === "Sunny") {
      return "fas fa-sun";
    } else if (classarg === "Mist") {
      return "fas fa-smog";
    } else if (classarg === "Thunderstorm" || classarg === "Drizzle") {
      return "fas fa-thunderstorm";
    } else {
      return "fas fa-cloud-sun";
    }
  };

  const reset = () => {
    if (searchInputBox.current) {
      searchInputBox.current.value = "";
    }
  };

  const addZero = (i) => {
    if (i < 10) {
      i = "0" + i;
    }
    return i;
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      getWeatherReport(searchInputBox.current.value);
    }
  };

  const handleButtonClick = () => {
    getWeatherReport(searchInputBox.current.value);
  };

  return (
    <div className="app-main" id="parent">
      <div className="header">
        <h4>Get Weather</h4>
      </div>
      <div className="searchInputBox">
        <input
          type="text"
          id="input-box"
          className="input-box"
          placeholder="Enter city name"
          ref={searchInputBox}
          onKeyPress={handleKeyPress}
        />
        <button id="btn" className="btn" onClick={handleButtonClick}>
          Search
        </button>
      </div>
      <div className="weather-body" id="weather-body" ref={weatherBodyRef}>
        {weatherData && (
          <>
            <div className="location-deatils">
              <div className="city" id="city">
                {weatherData.name}, {weatherData.sys.country}
              </div>
              <div className="date" id="date">
                {dateManage(new Date())}
              </div>
            </div>
            <div className="weather-status">
              <div className="temp" id="temp">
                {Math.round(weatherData.main.temp)}째C
              </div>
              <div className="weather" id="weather">
                {weatherData.weather[0].main}{" "}
                <i className={getIconClass(weatherData.weather[0].main)}></i>
              </div>
              <div className="min-max" id="min-max">
                {Math.floor(weatherData.main.temp_min)}째C (min) /{" "}
                {Math.ceil(weatherData.main.temp_max)}째C (max)
              </div>
              <div id="updated_on">Updated as of {getTime(new Date())}</div>
            </div>
            <hr />
            <div className="day-details">
              <div className="basic">
                Feels like {weatherData.main.feels_like}째C | Humidity{" "}
                {weatherData.main.humidity}% <br /> Pressure{" "}
                {weatherData.main.pressure} mb | Wind {weatherData.wind.speed} KMPH
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

// Main App component with routing
const App = () => {
  return (
    <BrowserRouter>
      <head>
        <meta charset="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>Weather Forecast</title>
        <link rel="icon" href="./images/logo.png" type="image/x-icon" />
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css"
          integrity="sha512-iBBXm8fW90+nuLcSKlbmrPcLa0OT92xO1BIsZ+ywDWZCvqsWgccV3gFoRBv0z+8dLJgyAHIhR35VZc2oM/gI1w=="
          crossOrigin="anonymous"
          referrerPolicy="no-referrer"
        />
      </head>
      <Routes>
        <Route path="/" element={<WeatherIntro />} />
        <Route path="/weather" element={<WeatherMain />} />
      </Routes>
    </BrowserRouter>
  );
};

export default weather;
