import React, { useEffect, useState } from "react";
import Newsitem from "./Newsitem";

const NewsBoard = ({category}) => {
	const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true); // New: Loading state
    const [error, setError] = useState(null);     // New: Error state

	useEffect(() => {
        setLoading(true); 
        setError(null);  

        const apiKey = process.env.REACT_APP_API_KEY;

        if (!apiKey) {
            console.error("API Key is not defined. Please set REACT_APP_API_KEY in your .env file in the project root.");
            setError("Configuration Error: API Key is missing. Please check your .env file.");
            setLoading(false);
            return;
        }

        let url = `https://newsapi.org/v2/top-headlines?country=us&category=${category}&apiKey=${apiKey}`;

		fetch(url)
		    .then(response => {
                console.log("API Response Status:", response.status); // For debugging
                if (!response.ok) {
                    return response.json().then(errorData => {
                        console.error("API Error Response Data:", errorData); // For debugging
                        throw new Error(`HTTP error! Status: ${response.status} - ${errorData.message || 'Unknown error'}`);
                    });
                }
                return response.json();
            })
			.then(data => {
                console.log("API Data received:", data); // For debugging
                if (data.status === 'ok' && Array.isArray(data.articles)) {
			        setArticles(data.articles);
                } else if (data.status === 'error') {
                    console.error("NewsAPI returned an error status:", data.code, data.message);
                    setError(`NewsAPI Error: ${data.message}`);
                } else {
                    console.error("API response does not contain a valid 'articles' array or unexpected status:", data);
                    setError("Unexpected API response format. Could not load articles.");
                }
		    })
			.catch(err => {
                console.error("Error fetching news:", err); 
                setError(`Failed to load news: ${err.message}. Please check your internet connection and API key.`);
            })
            .finally(() => {
                setLoading(false); 
            });
		}, [category]); 
    if (loading) {
        return <div className="text-center my-4">Loading news...</div>;
    }

    if (error) {
        return <div className="alert alert-danger text-center my-4">{error}</div>;
    }

    if (articles.length === 0) {
        return <div className="text-center my-4">No news found for the selected category or country.</div>;
    }

	return (
		<div>
			<h2 className="text-center">Latest <span className="badge bg-danger">News</span></h2>
            <div className="d-flex flex-wrap justify-content-center">
				{articles.map((news, index) => (
					<Newsitem
                        key={index}
                        title={news.title || "No Title Available"}
                        description={news.description || "No description available."}
                        src={news.urlToImage || "https://via.placeholder.com/360x200?text=No+Image"} // Fallback image
                        url={news.url}
                    />
				))}
            </div>
		</div>
	);
};

export default NewsBoard;
