import React, { useState, useEffect, useRef } from 'react';
// FontAwesomeIcon imports removed as they require specific npm packages not available in this environment

// News Card Component
const NewsCard = ({ article }) => {
  // Format the date for display
  const date = new Date(article.publishedAt).toLocaleString("en-GB", {
    hour: '2-digit',
    minute: '2-digit',
    day: '2-digit',
    month: 'short',
    year: 'numeric'
  });

  return (
    <div
      className="bg-white rounded-lg shadow-lg overflow-hidden cursor-pointer hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 flex flex-col"
      onClick={() => window.open(article.url, "_blank")}
    >
      <div className="relative h-48">
        <img
          src={article.urlToImage}
          alt={article.title || "News Image"}
          className="w-full h-full object-cover"
          // Fallback image if urlToImage is broken or missing
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = `https://placehold.co/600x400/e0e0e0/505050?text=No+Image`;
          }}
        />
      </div>
      <div className="p-4 flex-grow">
        <h3 className="text-xl font-bold mb-2 text-gray-900 line-clamp-2">
          {article.title}
        </h3>
        <h6 className="text-gray-600 text-sm mb-2">
          {`${article.source.name} - ${date}`}
        </h6>
        <p className="text-gray-700 text-sm line-clamp-3">
          {article.description}
        </p>
      </div>
    </div>
  );
};

// Main HomePage Component
const HomePage = () => {
  // NewsAPI key (should be handled securely in a real app, e.g., environment variables)
  const Api_key = "71146ebefc69425aa26e16e6e9e066d1";
  const url = "https://newsapi.org/v2/everything?q=";

  const [currentLanguage] = useState('en'); // Language is fixed to English
  const [articles, setArticles] = useState([]); // State to hold news articles
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true"); // Dark mode state
  const [currentNav, setCurrentNav] = useState(null); // Active navigation item
  const [message, setMessage] = useState(''); // State for displaying messages to the user

  const searchInputRef = useRef(null); // Ref for the search input field

  // Effect to load initial news and apply dark mode class on body
  useEffect(() => {
    fetchNews("India"); // Fetch news for "India" on initial load
  }, []); // Empty dependency array means this runs once on mount

  // Effect to toggle dark mode class on body and store preference in localStorage
  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    document.body.classList.toggle('dark-mode', darkMode);
    // Apply background color based on dark mode
    document.body.style.backgroundColor = darkMode ? '#1a202c' : '#f7fafc'; // Tailwind's gray-900 or gray-50
    document.body.style.color = darkMode ? '#e2e8f0' : '#2d3748'; // Tailwind's gray-200 or gray-800
  }, [darkMode]);

  // Function to fetch news articles
  const fetchNews = async (query) => {
    setMessage(''); // Clear previous messages
    try {
      const res = await fetch(`${url}${query}&apiKey=${Api_key}&language=${currentLanguage}`);
      if (!res.ok) {
        // Handle non-2xx responses
        throw new Error(`Network response was not ok: ${res.statusText} (Status: ${res.status})`);
      }
      const data = await res.json();
      if (data.articles && data.articles.length === 0) {
        setArticles([]);
        setMessage('No articles found for your query. Try a different search term.');
      } else if (data.articles) {
        // Filter out articles without an image to ensure better display
        setArticles(data.articles.filter(article => article.urlToImage));
        if (data.articles.filter(article => article.urlToImage).length === 0) {
            setMessage('No articles with images found for your query. Try a different search term.');
        }
      } else {
          setArticles([]);
          setMessage('No articles found. The API response was empty or malformed.');
      }
    } catch (error) {
      setMessage(`Error fetching news: ${error.message}. Please try again later.`);
      console.error('Error fetching news:', error);
      setArticles([]); // Clear articles on error
    }
  };

  // Handler for navigation item clicks
  const handleNavItemClick = (id) => {
    fetchNews(id);
    if (searchInputRef.current) {
      searchInputRef.current.value = ''; // Clear search input
    }
    setCurrentNav(id); // Set active navigation item
  };

  // Handler for search button click
  const handleSearch = () => {
    const query = searchInputRef.current?.value.trim();
    if (!query) {
      setMessage('Please enter a valid search term.');
      return;
    }
    fetchNews(query);
    setCurrentNav(null); // Clear active navigation item
  };

  // Handler for Enter key press in search input
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  // Toggle dark mode
  const toggleDarkMode = () => {
    setDarkMode(prev => !prev);
  };

  // Reload the page
  const handleReload = () => window.location.reload();

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-gray-200' : 'bg-gray-50 text-gray-800'} transition-colors duration-300 font-inter`}>
      <nav className={`py-4 shadow-md ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
        <div className="container mx-auto px-4 flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
          <a href="#" onClick={handleReload} className="flex-shrink-0">
            <img
              src="https://placehold.co/150x40/000000/FFFFFF?text=News+Logo" // Placeholder for logo16-1.png
              alt="Company Logo"
              className="h-10"
            />
          </a>

          <div className="flex-grow flex justify-center md:justify-start">
            <ul className="flex flex-wrap justify-center md:justify-start gap-x-6 gap-y-2 text-lg font-medium">
              <li><a href="./components/Add Note/Note.jsx" className="hover:text-blue-500 transition-colors duration-200">Add Note</a></li>
              <li
                className={`hover:text-blue-500 transition-colors duration-200 cursor-pointer ${currentNav === 'highlights' ? 'text-blue-600 font-bold' : ''}`}
                onClick={() => handleNavItemClick('highlights')}
              >
                Highlights
              </li>
              <li><a href="./components/bot/Bot_ai.jsx" className="hover:text-blue-500 transition-colors duration-200">Bot</a></li>
            </ul>
          </div>

          <div className="flex items-center space-x-4 flex-shrink-0">
            <div className="flex border border-gray-300 rounded-full overflow-hidden focus-within:ring-2 focus-within:ring-blue-500">
              <input
                id="search-text"
                type="text"
                className="w-full py-2 px-4 focus:outline-none text-gray-800 bg-white"
                placeholder="Search news..."
                onKeyPress={handleKeyPress}
                ref={searchInputRef}
              />
              <button
                className="bg-blue-500 text-white px-4 py-2 hover:bg-blue-600 transition-colors duration-200"
                onClick={handleSearch}
              >
                <img
                  src="https://placehold.co/24x24/FFFFFF/000000?text=🔎" // Placeholder for srch.png
                  alt="Search"
                  className="w-6 h-6"
                />
              </button>
            </div>

            <div className="flex items-center space-x-4">
              <div
                className="p-2 cursor-pointer rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors duration-200"
                onClick={toggleDarkMode}
              >
                {/* Font Awesome moon icon */}
                <i className={`fas fa-moon ${darkMode ? 'text-yellow-400' : 'text-gray-700'}`}></i>
              </div>
              <a href="../login and signup/logout.php" className="block p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors duration-200">
                {/* Font Awesome sign-out icon */}
                <i className="fas fa-sign-out-alt text-cadetblue-500"></i> {/* Using custom Tailwind color if defined */}
              </a>
            </div>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4 py-8">
        {/* Message Display Area */}
        {message && (
          <div className="bg-blue-100 border border-blue-400 text-blue-700 px-4 py-3 rounded relative mb-8" role="alert">
            <span className="block sm:inline">{message}</span>
          </div>
        )}

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6" id="cards-container">
          {articles.length > 0 ? (
            articles.map((article, index) => (
              <NewsCard key={article.url || index} article={article} /> // Use article.url as key if unique, fallback to index
            ))
          ) : (
            !message && <p className="col-span-full text-center text-lg text-gray-600 dark:text-gray-400">Loading news, please wait...</p>
          )}
        </div>
      </main>

