export default function LoginPage(){
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const logInUser = () => {
        if (email.length === 0) alert("Email has left Blank!");
        else if (password.length === 0) alert("password has left Blank!");
        else {
            axios.post('http://127.0.0.1:5000/login', { email, password })
                .then(() => navigate("/"))
                .catch(error => {
                    console.log(error);
                    if (error.response.status === 401) alert("Invalid credentials");
                });
        }
    }

    return (
        <div className="container">
            <div className="form-title">Login</div>
            <form>
                <div className="input-group">
                    <i className="fas fa-envelope"></i>
                    <input type="email" required placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
                    <label>Email</label>
                </div>
                <div className="input-group">
                    <i className="fas fa-lock"></i>
                    <input type="password" required placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
                    <label>Password</label>
                </div>
                <div className="recover">
                    <a href="#">Forgot password?</a>
                </div>
                <button type="button" className="btn" onClick={logInUser}>Login</button>
                <div className="or">or</div>
                <div className="links">
                    <button onClick={() => navigate('/register')}>Register</button>
                </div>
            </form>
        </div>
    );
}
