import axios from 'axios';
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import './LoginPage.css';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const navigate = useNavigate();

  const logInUser = () => {
    if (email.length === 0) {
      alert("Email has left Blank!");
    } else if (password.length === 0) {
      alert("password has left Blank!");
    } else {
      axios.post('http://127.0.0.1:5000/login', {
        email: email,
        password: password
      })
        .then(function (response) {
          console.log(response);
          // navigate("/"); to open app.js
          window.location.href = "../home/index.html";
        })
        .catch(function (error) {
          console.log(error, 'error');
          if (error.response.status === 401) {
            alert("Invalid credentials");
          }
        });
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h2 className="title">Log Into Your Account</h2>

        <div className="input-group">
          <i className="fas fa-envelope"></i>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            id="form3Example3"
            className="input-field"
            placeholder=" "
          />
          <label htmlFor="form3Example3" className="input-label">
            Email
          </label>
        </div>

        <div className="input-group">
          <i className="fas fa-lock"></i>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            id="form3Example4"
            className="input-field"
            placeholder=" "
          />
          <label htmlFor="form3Example4" className="input-label">
            Password
          </label>
        </div>

        <div className="checkbox-group">
          <div className="checkbox-wrapper">
            <input type="checkbox" id="form2Example3" className="checkbox" />
            <label htmlFor="form2Example3" className="checkbox-label">
              Remember me
            </label>
          </div>
          <a href="#!" className="forgot-link">
            Forgot password?
          </a>
        </div>

        <button type="button" onClick={logInUser} className="login-btn">
          Login
        </button>

        <p className="register-text">
          Don't have an account?{' '}
          <a href="/register" className="register-link">
            Register
          </a>
        </p>
      </div>
    </div>
  );
}
