import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const navigate = useNavigate();

  const logInUser = () => {
    if (email.length === 0) {
      alert("Email has left Blank!");
    } else if (password.length === 0) {
      alert("password has left Blank!");
    } else {
      axios.post('http://127.0.0.1:5000/login', {
        email: email,
        password: password
      })
        .then(function (response) {
          console.log(response);
          navigate("/");
        })
        .catch(function (error) {
          console.log(error, 'error');
          if (error.response.status === 401) {
            alert("Invalid credentials");
          }
        });
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-2xl shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-6">Log Into Your Account</h2>

        <div className="relative mb-6">
          <i className="bi bi-envelope absolute left-0 top-1/2 transform -translate-y-1/2 text-gray-500"></i>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            id="form3Example3"
            className="w-full pl-8 pb-1 border-b border-gray-300 focus:border-purple-600 outline-none transition-colors peer"
            placeholder=" "
          />
          <label
            htmlFor="form3Example3"
            className="absolute left-8 top-1/2 transform -translate-y-1/2 text-gray-500 transition-all peer-placeholder-shown:top-1/2 peer-placeholder-shown:text-gray-500 peer-focus:-top-3 peer-focus:text-purple-600 peer-focus:text-sm peer-[:not(:placeholder-shown)]:-top-3 peer-[:not(:placeholder-shown)]:text-purple-600 peer-[:not(:placeholder-shown)]:text-sm"
          >
            Email
          </label>
        </div>

        <div className="relative mb-6">
          <i className="bi bi-lock absolute left-0 top-1/2 transform -translate-y-1/2 text-gray-500"></i>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            id="form3Example4"
            className="w-full pl-8 pb-1 border-b border-gray-300 focus:border-purple-600 outline-none transition-colors peer"
            placeholder=" "
          />
          <label
            htmlFor="form3Example4"
            className="absolute left-8 top-1/2 transform -translate-y-1/2 text-gray-500 transition-all peer-placeholder-shown:top-1/2 peer-placeholder-shown:text-gray-500 peer-focus:-top-3 peer-focus:text-purple-600 peer-focus:text-sm peer-[:not(:placeholder-shown)]:-top-3 peer-[:not(:placeholder-shown)]:text-purple-600 peer-[:not(:placeholder-shown)]:text-sm"
          >
            Password
          </label>
        </div>

        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center">
            <input type="checkbox" id="form2Example3" className="mr-2" />
            <label htmlFor="form2Example3" className="text-sm text-gray-600">Remember me</label>
          </div>
          <a href="#!" className="text-sm text-purple-600 hover:underline">Forgot password?</a>
        </div>

        <button
          type="button"
          onClick={logInUser}
          className="w-full bg-purple-600 text-white py-3 rounded-xl hover:bg-purple-700 transition-colors"
        >
          Login
        </button>

        <p className="text-center mt-4 text-sm text-gray-600">
          Don't have an account?{' '}
          <a href="/register" className="text-purple-600 hover:underline">Register</a>
        </p>
      </div>
    </div>
  );
}
