import React, { useState, useEffect, useRef } from "react";
import "./style.css";

const bot_ai = () => {
  const [content, setContent] = useState("Click here for talk to me");
  const [voiceVisible, setVoiceVisible] = useState(false);
  const [btnVisible, setBtnVisible] = useState(true);
  const recognitionRef = useRef(null);

  useEffect(() => {
    // Initialize speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.onresult = (event) => {
        const currentIndex = event.resultIndex;
        const transcript = event.results[currentIndex][0].transcript;
        setContent(transcript);
        takeCommand(transcript.toLowerCase());
      };
    }

    // Call wishMe on component mount
    wishMe();

    // Cleanup recognition on unmount
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const speak = (text) => {
    const text_speak = new SpeechSynthesisUtterance(text);
    text_speak.rate = 1;
    text_speak.pitch = 1;
    text_speak.volume = 1;
    text_speak.lang = "hi-GB";
    window.speechSynthesis.speak(text_speak);
  };

  const wishMe = () => {
    const day = new Date();
    const hours = day.getHours();
    if (hours >= 0 && hours < 12) speak("Good Morning Sir");
    else if (hours >= 12 && hours < 16) speak("Good afternoon Sir");
    else speak("Good Evening Sir");
  };

  const takeCommand = (message) => {
    setVoiceVisible(false);
    setBtnVisible(true);
    if (message.includes("hello") || message.includes("hey")) {
      speak("hello sir, what can i help you?");
    } else if (message.includes("who are you")) {
      speak("i am virtual assistant, created by Revathi");
    } else if (message.includes("open youtube")) {
      speak("opening youtube...");
      window.open("https://youtube.com/", "_blank");
    } else if (message.includes("open google")) {
      speak("opening google...");
      window.open("https://google.com/", "_blank");
    } else if (message.includes("open facebook")) {
      speak("opening facebook...");
      window.open("https://facebook.com/", "_blank");
    } else if (message.includes("open instagram")) {
      speak("opening instagram...");
      window.open("https://instagram.com/", "_blank");
    } else if (message.includes("open calculator")) {
      speak("opening calculator..");
      window.open("calculator://");
    } else if (message.includes("open whatsapp")) {
      speak("opening whatsapp..");
      window.open("whatsapp://");
    } else if (message.includes("time")) {
      const time = new Date().toLocaleString(undefined, {
        hour: "numeric",
        minute: "numeric",
      });
      speak(time);
    } else if (message.includes("date")) {
      const date = new Date().toLocaleString(undefined, {
        day: "numeric",
        month: "short",
      });
      speak(date);
    } else {
      const finalText =
        "this is what i found on internet regarding " +
        message.replace("Revathi", "") ||
        message.replace("Revathi", "");
      speak(finalText);
      window.open(
        `https://www.google.com/search?q=${message.replace("Revathi", "")}`,
        "_blank"
      );
    }
  };

  const handleButtonClick = () => {
    if (recognitionRef.current) {
      recognitionRef.current.start();
      setVoiceVisible(true);
      setBtnVisible(false);
    }
  };

  return (
    <>
      <head>
        <meta charset="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>Revathi, My virtual assistant</title>
        <link rel="shortcut icon" href="logo.jpg" type="image/x-icon" />
      </head>
      <body>
        <h1>
          I'm <span id="name">Revathi</span>, Your{" "}
          <span id="va">Virtual Assistant</span>
        </h1>
        <img
          src="voice.gif"
          alt="voice"
          id="voice"
          style={{ display: voiceVisible ? "block" : "none" }}
        />
        <button
          id="btn"
          onClick={handleButtonClick}
          style={{ display: btnVisible ? "flex" : "none" }}
        >
          <img src="mic.svg" alt="mic" />
          <span id="content">{content}</span>
        </button>
      </body>
    </>
  );
};

export default bot_ai;
