// frontend/src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

// Import your HomePage
import HomePage from './components/Home/HomePage.jsx';
import WeatherApp from './components/Weather-Forecast/WeatherApp.jsx'; // <--- Import it here

// Import other components if they are also pages (e.g., Add_note, Bot)
import AddNote from './components/Add_note/Note.jsx';
import BotAi from './components/bot/Bot_ai.jsx';

import './App.css';

function App() {
  return (
    <Router> {/* This wraps your entire application */}
      <div className="App">
        <Routes> {/* This holds all your individual routes */}
          {/* Main Home Page - News App (rendered when path is '/') */}
          <Route path="/" element={<HomePage />} />

          {/* Weather Forecast Page (rendered when path is '/weather') */}
          <Route path="/weather" element={<WeatherApp />} /> {/* <--- Add this Route */}

          {/* Add Note Page (rendered when path is '/add-note') */}
          <Route path="/add-note" element={<AddNote />} />

          {/* Bot Page (rendered when path is '/bot') */}
          <Route path="/bot" element={<BotAi />} />

          {/* You can add more routes here for other features */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
